import React,{ useContext } from 'react'
import { ShopContext } from '../Context/ShopContext';
import { useParams } from 'react-router-dom';
import Breadcrum from '../Compenents/Breadcrum/Breadcrum';
import ProductDisplay from '../Compenents/ProductDisplay/ProductDisplay';
//import all_product from '../Compenents/Assets/all_product';
const Product=(props)=> {
  const {all_product}=useContext(ShopContext);
  //const {productId}=useParams();
 const product = all_product.find((e)=> e.id === Number(props.id));
   
        
     
  return (
    <div>
      <Breadcrum product={product}/>

      <ProductDisplay product={product}/>
    </div>
  )
}
export default  Product;
