import React,{useContext} from 'react'
import './CSS/ShopCategory.css'
import { ShopContext } from '../Context/ShopContext'
//import dropdown_icon from '../Compenents/Assets'
import Item from '../Compenents/Item/Item'

export default function ShopCategory(props) {
const{all_product}=useContext(ShopContext);



  return (
    <div className='shop-category'>
      <div calssName='shop'>
        <h1>FLAT 50% OFF</h1>
      <p>12 Hours 20 Mins</p>
      <button>Expece Now</button>
      <img src={props.banner===props.category} alt="" />
     
     
      </div>
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-12</span>out of 36 products
        </p>
        
      </div>
      <div className="shopcategory-products">
        {all_product.map((item,i)=>{
          if(props.category===item.category){
            return(
              <div>
                <Item className='item' key={i} id={item.id} name={item.name} image={item.image} new_price={item.new_price} old_price={item.old_price}/>
              </div>
            )
            

          
          }
          else{
            return null;
          }
        })}
      </div>
    </div>
  )
}
