import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedCoffee, setSelectedCoffee] = useState(null);
  const [reservationDate, setReservationDate] = useState('');
  const [reservedCoffees, setReservedCoffees] = useState([]);

  const coffeeMenu = [
    { id: 1, name: 'Espresso', price: 3.5 },
    { id: 2, name: 'Latte', price: 4 },
    { id: 3, name: 'Cappuccino', price: 4.5 }
  ];

  const handleSelectCoffee = (coffee) => {
    setSelectedCoffee(coffee);
  };

  const handleReservationDateChange = (e) => {
    setReservationDate(e.target.value);
  };

  const handleReserveCoffee = () => {
    if (selectedCoffee && reservationDate !== '') {
      const newReservation = {
        coffee: selectedCoffee,
        date: reservationDate
      };

      setReservedCoffees([...reservedCoffees, newReservation]);
      setSelectedCoffee(null);
      setReservationDate('');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Notre Café</h1>
        <h2>Menu</h2>
        <ul>
          {coffeeMenu.map(coffee => (
            <li key={coffee.id} onClick={() => handleSelectCoffee(coffee)}>
              {coffee.name} - ${coffee.price}
            </li>
          ))}
        </ul>
        {selectedCoffee && (
          <div>
            <h2>Réserver {selectedCoffee.name}</h2>
            <input
              type="date"
              value={reservationDate}
              onChange={handleReservationDateChange}
            />
            <button onClick={handleReserveCoffee}>Réserver</button>
          </div>
        )}
<h2>Réservations</h2>
        <ul>
          {reservedCoffees.map((reservation, index) => (
            <li key={index}>
              {reservation.coffee.name} le {reservation.date}
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
