import React,{ useContext } from 'react'
import { ShopContext } from '../Context/ShopContext';
import { useParams } from 'react-router-dom';
import Breadcrum from '../Compenents/Breadcrum/Breadcrum';
import ProductDisplay from '../Compenents/ProductDisplay/ProductDisplay';

const Product=()=> {
  const {all_product}=useContext(ShopContext);
  const {productId}=useParams();
  const prod = all_product.filter(e=> e.id === Number(productId));
  return (
    <div>
      <Breadcrum product= {prod}/>
      <ProductDisplay product={prod}/>
    </div>
  )
}
export default  Product;
